import React, { Component, useState } from 'react';
import { View, Text, SafeAreaView, StyleSheet, ScrollView, TextInput } from 'react-native';
import AsyncStorage from '@react-native-community/async-storage';
import Axios from 'axios';
import { Card, ListItem, Button, Icon } from 'react-native-elements';
import { getbannerdata } from './viewyourstory'
import Textarea from 'react-native-textarea';
import { cos } from 'react-native-reanimated';

export default class EditScreen extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            data: [],
            title: '',
            storypara: ''
        }
    }
    getcurrentstories = async () => {
        try {
            let stories = await AsyncStorage.getItem('currentstory');
            const story = JSON.parse(stories);
            console.log(story);
            this.setState({ data: story });
            let users = await AsyncStorage.getItem('userdatas');
            const userdatas = JSON.parse(users);
            console.log(userdatas);
            this.setState({ data: userdatas });
            const gettoken = this.state.data.token;
            // const getuserid = this.state.data.userid;
            const stories_id = story.stories_id;
            const localurl = 'http://localhost:8000/api/edit/';
            const config = {
                headers: {
                    'content-type': 'multipart/form-data',
                    'Authorization': 'Bearer ' + gettoken,
                    'Accept': 'application/json',
                },

            };
            Axios.get(localurl + stories_id, config)
                .then(response => {
                    // this.setState({ profiles: response.data.profile });
                    // console.log(response.data.profile.useremail)
                    console.log(response);
                })
                .catch(function (error) {
                    console.log(error);
                })
        } catch (e) {
            // error reading value
            console.log(e);
        }
    }
    componentDidMount() {
        this.getcurrentstories();
    }
    updatestories = async () => {
        try {
            let stories = await AsyncStorage.getItem('currentstory');
            const story = JSON.parse(stories);
            console.log(story);
            const stories_id = story.stories_id;
            const { storypara } = this.state.storypara;
            const { title } = this.stories.title
            Axios.post(`http://localhost:8000/api/update/${stories_id}`, title, storypara).then(response => {
                console.log(response);
            })

        } catch (e) {
            // error reading value
            console.log(e);
        }
    }
    deletestories = async () => {
        try {
            let users = await AsyncStorage.getItem('userdatas');
            const userdatas = JSON.parse(users);
            console.log(userdatas);
            // this.setState({ data: userdatas });
            const gettoken = userdatas.token;
            const getuserid = userdatas.userid;
            let stories = await AsyncStorage.getItem('currentstory');
            const story = JSON.parse(stories);
            console.log(story.stories_id)
            const stories_id = story.stories_id;
            const config = {
                headers: {
                    // 'content-type': 'multipart/form-data',
                    'Authorization': 'Bearer ' + gettoken,
                    'Accept': 'application/json',
                },

            };
            // const deleteurl = 'http://192.168.1.4:8000/api/deletstory/';
            const deleteurl = 'http://localhost:8000/api/deletstory';
            fetch(deleteurl + stories_id, config, {
                method: 'DELETE',
                // headers: {
                //     Accept: 'application/json',
                //     'Content-Type': 'application/json',
                //     'Authorization': 'Bearer ' + gettoken
                // },
            })
        } catch (e) {
            // error reading value
            console.log(e);
        }
    }
    render() {
        // getbannerdata().then(res => {
        //     console.log(res);
        // })
        return (
            <SafeAreaView style={{ flex: 1 }}>
                <ScrollView>
                    <View>
                        <Text>Story details</Text>
                    </View>
                    <View>
                        <Text style={styles.storytitle}>Title</Text>
                        {/* <Text style={styles.titleanswer}>{this.state.data.title}</Text> */}
                        <TextInput style={styles.titleanswer}
                            onChangeText={title => this.setState({ title })}
                        />
                    </View>
                    <View>
                        <Text style={styles.contenthead}>Content</Text>
                        <Textarea
                            containerStyle={styles.textareaContainer}
                            style={styles.textarea}
                            defaultValue={this.state.data.storycontent}
                            onChangeText={storypara => this.setState({ storypara })}
                        />
                        {/* <Text style={styles.contentanswer}>{this.state.data.storycontent}</Text> */}
                    </View>
                    <Text onPress={this.updatestories}>update</Text>
                    <View>
                        <Text onPress={this.deletestories}>delete</Text>
                    </View>
                </ScrollView>
            </SafeAreaView>
        )
    }

}

const styles = StyleSheet.create({
    storycontentbottomview: {
        paddingBottom: 50,
    },
    mainBody: {
        paddingBottom: 50
    },
    storytitle: {
        textAlign: 'center',
        fontWeight: 'bold',
        paddingBottom: 15
    },
    titleanswer: {
        color: '#003666',
        textAlign: 'center',
        borderWidth: 1,
        borderRadius: 8,
        borderColor: '#dadae8',
        paddingBottom: 3,
        paddingTop: 3,
        paddingLeft: 3,
        paddingRight: 3,
        width: '70%',
        marginLeft: 'auto',
        marginRight: 'auto'
    },
    contenthead: {
        textAlign: 'center',
        fontWeight: 'bold',
        paddingBottom: 15
    },
    contentanswer: {
        color: '#136ad5',
        textAlign: 'justify',
        paddingLeft: 5,
        paddingRight: 5
    },
    textareaContainer: {
        height: 300,
        padding: 5,
        marginTop: 20,
        width: 300
    },
    textarea: {
        // textAlignVertical: 'top',  // hack android
        // height: 570,
        fontSize: 14,
        color: '#333',
        borderWidth: 1,
        borderRadius: 8,
        borderColor: '#dadae8',
    },
});
