import React, { useState, createRef } from 'react';
import {
    StyleSheet,
    TextInput,
    View,
    Text,
    Image,
    KeyboardAvoidingView,
    Keyboard,
    TouchableOpacity,
    ScrollView,
} from 'react-native';
import AsyncStorage from '@react-native-community/async-storage';
import Axios from 'axios';

import Loader from './loader';

const Storycreatescreen = (props) => {
    const [username, setusername] = useState('');
    const [useremail, setuseremail] = useState('');
    const [title, settitle] = useState('');
    const [storypara, setstorypara] = useState('');
    // const [password, setpassword] = useState('');
    // const [loading, setLoading] = useState(false);
    const [errortext, setErrortext] = useState('');
    const [
        isRegistraionSuccess,
        setIsRegistraionSuccess
    ] = useState(false);

    const emailInputRef = createRef();
    const ageInputRef = createRef();
    const addressInputRef = createRef();
    const passwordInputRef = createRef();

    const handleSubmitButton = async () => {
        setErrortext('');
        if (!username) {
            alert('Please fill Name');
            return;
        }
        if (!useremail) {
            alert('Please fill Email');
            return;
        }
        if (!userage) {
            alert('Please fill Age');
            return;
        }
        if (!useraddress) {
            alert('Please fill Address');
            return;
        }
        if (!password) {
            alert('Please fill Password');
            return;
        }
        //Show Loader
        // setLoading(true);
        try {
            let users = await AsyncStorage.getItem('userdatas');
            const userdatas = JSON.parse(users);
            console.log(userdatas);
            // this.setState({ data: userdatas });
            const gettoken = userdatas.token;
            const getuserid = userdatas.userid;
            console.log(gettoken);
            const geturl = 'http://192.168.1.4:8000/api/getuserdetails/';
            const config = {
                headers: {
                    'content-type': 'multipart/form-data',
                    'Authorization': 'Bearer ' + gettoken,
                    'Accept': 'application/json',
                },

            };
            Axios.get(geturl + getuserid, config)
                .then(response => {
                    // this.setState({ profiles: response.data.profile });
                    console.log(response.data.profile.useremail)
                    const url = 'http://192.168.1.4:8000/api/createstory';
                    fetch(url, {
                        method: 'POST',
                        headers: {
                            Accept: 'application/json',
                            'Content-Type': 'application/json'
                        },
                        body: JSON.stringify({
                            username: response.data.profile.username,
                            useremail: response.data.profile.useremail,
                            // password: password,
                            title: title,
                            storypara: storypara,
                        })
                    })
                        .then((response) => response.json())
                        .then((responseJson) => {
                            //Hide Loader
                            // setLoading(false);
                            console.log(responseJson);
                            if (responseJson.message === 'success') {
                                setIsRegistraionSuccess(true);
                                console.log(
                                    'Registration Successful. Please Login to proceed'
                                );
                            } else {
                                setErrortext(responseJson.msg);
                            }
                        })
                        .catch((error) => {
                            //Hide Loader
                            // setLoading(false);
                            console.error(error);
                        });

                })
                .catch(function (error) {
                    console.log(error);
                })
        } 
        catch (e) {
            // error reading value
            console.log(e);
        }
    }
    if (isRegistraionSuccess) {
        return (
            <View
                style={{
                    flex: 1,
                    backgroundColor: '#307ecc',
                    justifyContent: 'center',
                }}>
                <Image
                    // source={require('../Image/success.png')}
                    style={{
                        height: 150,
                        resizeMode: 'contain',
                        alignSelf: 'center'
                    }}
                />
                <Text style={styles.successTextStyle}>
                    Registration Successful
        </Text>
                <TouchableOpacity
                    style={styles.buttonStyle}
                    activeOpacity={0.5}
                    onPress={() => props.navigation.navigate('LoginScreen')}>
                    <Text style={styles.buttonTextStyle}>Login Now</Text>
                </TouchableOpacity>
            </View>
        );
    }
    return (
        <View style={{ flex: 1, backgroundColor: '#307ecc' }}>
            {/* <Loader loading={loading} /> */}
            <ScrollView
                keyboardShouldPersistTaps="handled"
                contentContainerStyle={{
                    justifyContent: 'center',
                    alignContent: 'center',
                }}>
                <KeyboardAvoidingView enabled>
                    {/* <View style={styles.SectionStyle}>
                        <TextInput
                            style={styles.inputStyle}
                            onChangeText={(username) => setusername(username)}
                            underlineColorAndroid="#f000"
                            placeholder="Enter Name"
                            placeholderTextColor="#8b9cb5"
                            autoCapitalize="sentences"
                            returnKeyType="next"
                        />
                    </View> */}
                    {/* <View style={styles.SectionStyle}>
                        <TextInput
                            style={styles.inputStyle}
                            onChangeText={(useremail) => setuseremail(useremail)}
                            underlineColorAndroid="#f000"
                            placeholder="Enter Email"
                            placeholderTextColor="#8b9cb5"
                            keyboardType="email-address"
                            ref={emailInputRef}
                            returnKeyType="next"
                        />
                    </View> */}
                   
                    <View style={styles.SectionStyle}>
                        <TextInput
                            style={styles.inputStyle}
                            onChangeText={(title) => settitle(title)}
                            underlineColorAndroid="#f000"
                            placeholder="Enter Title"
                            placeholderTextColor="#8b9cb5"
                            returnKeyType="next"
                        />
                    </View>
                    <View style={styles.SectionStyle}>
                        <TextInput
                            style={styles.inputStyle}
                            onChangeText={(storypara) =>
                                setstorypara(storypara)
                            }
                            underlineColorAndroid="#f000"
                            placeholder="Enter content"
                            placeholderTextColor="#8b9cb5"
                            autoCapitalize="sentences"
                            returnKeyType="next"
                        />
                    </View>
                    {errortext != '' ? (
                        <Text style={styles.errorTextStyle}>
                            {errortext}
                        </Text>
                    ) : null}
                    <TouchableOpacity
                        style={styles.buttonStyle}
                        activeOpacity={0.5}
                        onPress={handleSubmitButton}>
                        <Text style={styles.buttonTextStyle}>REGISTER</Text>
                    </TouchableOpacity>
                </KeyboardAvoidingView>
            </ScrollView>
        </View>
    );
};
export default Storycreatescreen;

const styles = StyleSheet.create({
    SectionStyle: {
        flexDirection: 'row',
        height: 40,
        marginTop: 20,
        marginLeft: 35,
        marginRight: 35,
        margin: 10,
