import React, { Component, useState } from 'react';
import { View, Text, SafeAreaView, StyleSheet, ScrollView } from 'react-native';
import AsyncStorage from '@react-native-community/async-storage';
import Axios from 'axios';
import { Card, ListItem, Button, Icon } from 'react-native-elements';
import { getbannerdata } from './viewyourstory'

export default class EditScreen extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            data: []
        }
    }
    getcurrentstories = async () => {
        try {
            let stories = await AsyncStorage.getItem('currentstory');
            const story = JSON.parse(stories);
            console.log(story);
            this.setState({ data: story });
        } catch (e) {
            // error reading value
            console.log(e);
        }
    }
    componentDidMount() {
        this.getcurrentstories();
    }
    updatestories = async () => {
        // setErrortext('');
        try {
            let users = await AsyncStorage.getItem('userdatas');
            const userdatas = JSON.parse(users);
            console.log(userdatas);
            // this.setState({ data: userdatas });
            const gettoken = userdatas.token;
            const getuserid = userdatas.userid;
            let stories = await AsyncStorage.getItem('currentstory');
            const story = JSON.parse(stories);
            console.log(story.stories_id)
            const stories_id = story.stories_id;
            const username = story.username;
            const useremail = story.useremail;
            const title = story.title;
            const storypara = story.storycontent;
            const config = {
                headers: {
                    'content-type': 'multipart/form-data',
                    'Authorization': 'Bearer ' + gettoken,
                    'Accept': 'application/json',
                },

            }; 
            const url = 'http://192.168.1.4:8000/api/storiesupdate';
            // fetch(url+storyid,config, {
            //     method: 'POST',
            //     // headers: {
            //     //     Accept: 'application/json',
            //     //     'Content-Type': 'application/json',
            //     //     'Authorization': 'Bearer ' + gettoken
            //     // },
            //     body: JSON.stringify({
            //         username: response.data.profile.username,
            //         useremail: response.data.profile.useremail,
            //         title: title,
            //         storypara: storypara,
            //     })
            // })
        } catch (e) {
            // error reading value
            console.log(e);
        }
    }
    render() {
        // getbannerdata().then(res => {
        //     console.log(res);
        // })
        return (
            <SafeAreaView style={{ flex: 1 }}>
                <ScrollView>
                    <View>
                        <Text>Story details</Text>
                    </View>
                    <View>
                        <Text>Title</Text>
                        <Text>{this.state.data.title}</Text>
                    </View>
                    <View>
                        <Text>Content</Text>
                        <Text>{this.state.data.storycontent}</Text>
                    </View>
                    <Text onPress={this.updatestories}>update</Text>
                </ScrollView>
            </SafeAreaView>
        )
    }

}

const styles = StyleSheet.create({
    storycontentbottomview: {
        paddingBottom: 50,
    },
    mainBody: {
        paddingBottom: 50
    }
});
